@router.get("/mcp/sse")
async def mcp_sse_endpoint(request: Request):
    """
    MCP SSE 端点
    供外部 MCP 客户端通过 SSE (Server-Sent Events) 协议连接

    这是标准的 MCP 通信端点，兼容 Claude Desktop 等 MCP 客户端
    """
    logger.info(f"新的 MCP SSE 连接: {request.client.host}")

    async def event_generator():
        """生成 SSE 事件流"""
        try:
            # 创建内存队列用于双向通信
            from asyncio import Queue
            read_queue: Queue = Queue()
            write_queue: Queue = Queue()

            # 启动 MCP Server 处理任务
            async def run_mcp():
                """运行 MCP Server 消息处理循环"""
                try:
                    # 创建读写流
                    from mcp.server.stdio import StdioServerTransport

                    # 注意：这里我们需要使用自定义的传输层
                    # 因为 SSE 是单向的，我们需要处理客户端消息

                    # 简化实现：直接响应初始化
                    init_response = {
                        "jsonrpc": "2.0",
                        "id": 1,
                        "result": {
                            "protocolVersion": "2024-11-05",
                            "capabilities": {
                                "tools": {}
                            },
                            "serverInfo": {
                                "name": "kobe-tools",
                                "version": "1.0.0"
                            }
                        }
                    }
                    await write_queue.put(init_response)

                    # 持续监听消息
                    while True:
                        # 从客户端接收消息
                        if not read_queue.empty():
                            message = await read_queue.get()
                            logger.debug(f"收到 MCP 消息: {message}")

                            # 处理消息并生成响应
                            # TODO: 完整实现 MCP 协议消息处理

                        await asyncio.sleep(0.1)

                except Exception as e:
                    logger.error(f"MCP 处理错误: {e}")

            # 启动 MCP 处理任务
            mcp_task = asyncio.create_task(run_mcp())

            try:
                # 发送初始连接事件
                yield {
                    "event": "connected",
                    "data": json.dumps({
                        "server": "kobe-tools",
                        "status": "ready"
                    })
                }

                # 持续发送来自 MCP Server 的消息
                while True:
                    if await request.is_disconnected():
                        logger.info("MCP 客户端断开连接")
                        break

                    # 从写队列获取消息并发送
                    if not write_queue.empty():
                        message = await write_queue.get()
                        yield {
                            "event": "message",
                            "data": json.dumps(message)
                        }

                    await asyncio.sleep(0.1)

            finally:
                mcp_task.cancel()
                logger.info("MCP SSE 连接已关闭")

        except Exception as e:
            logger.error(f"SSE 事件生成器错误: {e}")
            yield {
                "event": "error",
                "data": json.dumps({"error": str(e)})
            }

    return EventSourceResponse(event_generator())


@router.post("/mcp/tools/call")
async def mcp_tool_call_http(request: Request):
    """
    HTTP 方式的工具调用端点（简化版）
    方便测试和直接调用，不需要完整的 MCP 协议握手

    请求体：
    {
        "name": "工具名称",
        "arguments": {...}
    }
    """
    body = await request.json()
    tool_name = body.get("name")
    arguments = body.get("arguments", {})

    if not tool_name:
        return {"error": "缺少工具名称"}

    logger.info(f"HTTP 工具调用: {tool_name}")

    # 调用工具
    result = await call_tool(tool_name, arguments)

    return {
        "tool": tool_name,
        "result": [
            {"type": item.type, "text": item.text}
            for item in result
        ]
    }


