{"command":["pwsh.exe","-NoProfile","-Command","$p='D:\AI_Projects\Kobe\scripts\run_telegram_extraction.ps1'; $content = Get-Content -LiteralPath $p -Raw; $start = $content.IndexOf("# If no OPENAI_API_KEY"); $braceOpen = $content.IndexOf("{", $start); $braceClose = $content.IndexOf("  }", $braceOpen); $old = $content.Substring($start, $braceClose + 3 - $start); $new = @'
  # If no OPENAI_API_KEY, run a local heuristic extractor (no LLM).
  if (-not $env:OPENAI_API_KEY) {
    # Parse essentials from the prompt (restrict to INPUT:...TOOLS: block)
    $inputIdx = $NextPrompt.IndexOf('INPUT:')
