      $hasBiz = $false
      foreach ($kw in $bizKeywords) { if ($text -like "*${kw}*") { $hasBiz = $true; break } }
      if ($hasBiz) { $bizSnippets.Add("- " + ($text.Trim())) }

      foreach ($rx in $priceRegexes) {
        $m = [regex]::Match($text, $rx)
