{"output":"Param(
  [string]$SourceDir = "D:\AI_Projects\.TelegramChatHistory\KB\services",
  [string]$WorkspaceDir = "D:\AI_Projects\.TelegramChatHistory\Workspace",
  [string]$WorkPlanDir = "D:\AI_Projects\.TelegramChatHistory\Workspace\.WorkPlan",
  [string]$TaskList = ".\CurrentBatch.md",
  [int]$BatchSize = 10
)

$ErrorActionPreference = 'Stop'

function Write-Info($msg){ Write-Host "[INFO] $msg" -ForegroundColor Cyan }
function Write-Warn($msg){ Write-Host "[WARN] $msg" -ForegroundColor Yellow }
function Write-Err($msg){ Write-Host "[ERROR] $msg" -ForegroundColor Red }

$genScript = Join-Path $WorkPlanDir 'GenerateNextBatch.ps1'
if(!(Test-Path $genScript)){ throw "GenerateNextBatch.ps1 not found at $genScript" }

# Global KB files
$kbRoot = Join-Path $WorkspaceDir 'VisaAchknowlegeBase'
$indexFile = Join-Path $kbRoot 'Index.yaml'
$presetFile = Join-Path $kbRoot 'PresetServiceCollections.yaml'
$ambigFile = Join-Path $kbRoot 'AmbiguousTermsDictionary.yaml'

if(!(Test-Path $indexFile -PathType Leaf)){ Write-Warn "Index.yaml missing; creating a minimal one."; "knowledge_base:`n  name: 'Visa KB'`n" | Set-Content $indexFile -Encoding UTF8 }
if(!(Test-Path $presetFile -PathType Leaf)){ Write-Warn "PresetServiceCollections.yaml missing; creating."; "collections: []`n" | Set-Content $presetFile -Encoding UTF8 }
if(!(Test-Path $ambigFile -PathType Leaf)){ Write-Warn "AmbiguousTermsDictionary.yaml missing; creating."; "terms: {}`n" | Set-Content $ambigFile -Encoding UTF8 }

function Get-DepartmentForFile($name){
  $n = $name.ToLower()
  if($n -match '13a' -or $n -match 'ǩ֤' -or $n -match 'ecc' -or $n -match 'i-?card' -or $n -match 'aepȡ����ǩ') { return 'BearuOfImmigration' }
  if($n -match 'aep' -or $n -match 'labor') { return 'DepartmentOfLabor' }
  if($n -match 'nbi' -or $n -match 'doj') { return 'DepartmentOfJustice' }
  if($n -match 'passport' -or $n -match 'dfa' -or $n -match 'apostille') { return 'DepartmentOfForeignAffair' }
  if($n -match 'payment' -or $n -match 'dragonpay' -or $n -match 'bdo') { return 'PaymentServices' }
  if($n -match 'notar' -or $n -match 'certif' -or $n -match 'document') { return 'DocumentServices' }
  return 'GeneralServices'
}

function Ensure-DepartmentFolder($dept){
  $path = Join-Path $kbRoot $dept
  if(!(Test-Path $path)){ New-Item -ItemType Directory -Path $path | Out-Null }
  return $path
}

function Make-Slug([string]$text){
  $t = $text
  $t = [regex]::Replace($t,'\.[mM][dD]$','')
  $t = $t -replace '\s+', '-'
  $t = $t -replace '[^a-zA-Z0-9\-]','-'
  $t = $t.ToLower()
  $t = [regex]::Replace($t,'-+','-').Trim('-')
  if([string]::IsNullOrWhiteSpace($t)){ $t = (Get-Random).ToString() }
  return $t
}

function Update-IndexYaml($dept, $bizName, $relPath){
  $yaml = Get-Content $indexFile -Raw -ErrorAction SilentlyContinue
  if([string]::IsNullOrWhiteSpace($yaml)){ $yaml = "knowledge_base:`n  name: 'Visa KB'`n" }

  if($yaml -notmatch ("(?ms)^\s*" + [regex]::Escape($dept) + ":\s*")){
    $yaml += ("`n  {0}:`n    full_name: \"\"`n    description: \"\"`n    folder_path: \"{0}/\"`n    solo_tasks:`n" -f $dept)
  }

  if($yaml -notmatch [regex]::Escape($relPath)){
    $entry = ("      - name: \"{0}\"`n        path: \"{1}\"`n        type: \"solo_task\"`n" -f $bizName, $relPath)
    $yaml += ("`n  # auto-added`n  {0}:`n    solo_tasks:`n{1}" -f $dept, $entry)
  }

  Set-Content $indexFile $yaml -Encoding UTF8
}

function Create-OrUpdate-BusinessDoc($dept, $sourcePath){
  $srcName = [IO.Path]::GetFileNameWithoutExtension($sourcePath)
  $slug = Make-Slug $srcName
  $deptDir = Ensure-DepartmentFolder $dept
  $target = Join-Path $deptDir ("$slug.md")
  $today = (Get-Date -Format 'yyyy-MM-dd')

  if(!(Test-Path $target)){
    $front = @(
      '---',
      "name: \"$srcName\"",
      "slug: \"$slug\"",
      'type: "solo_task"',
      "department: \"$dept\"",
      'documents_must_provide: []',
      'documents_can_produce: []',
      'documents_output: []',
      'related_businesses: []',
      'aliases: []',
      "updated_at: \"$today\"",
      '---',
      '',
      "# $srcName",
      '',
      '## Summary',
      'TBD. Extract details from source evidence.',
      '',
      '## Prerequisites',
      '- Geography: TBD',
      '- Relationship: TBD',
      '- Prior State: Valid passport, etc.',
      '',
      '## Required Documents',
      '- TBD',
      '',
      '## Outputs',
      '- TBD',
      '',
      '## Steps',
      '1. TBD',
      ''
    ) -join "`n"
    Set-Content $target $front -Encoding UTF8
    Write-Info "Created business doc: $target"
  } else {
    $c = Get-Content $target -Raw
    $c = $c -replace '(?m)^updated_at:\s*".*?"','updated_at: "' + $today + '"'
    Set-Content $target $c -Encoding UTF8
    Write-Info "Updated business doc: $target"
  }

  $relPath = [IO.Path]::Combine($dept, ("$slug.md")).Replace('\\','/')
  Update-IndexYaml -dept $dept -bizName $srcName -relPath $relPath
}

function Mark-Task-Complete($taskFile, $item){
  $content = Get-Content $taskFile -Raw
  $escaped = [regex]::Escape($item)
  $content = [regex]::Replace($content, "\[ \]\s*$escaped", "[x] $item")
  Set-Content $taskFile $content -Encoding UTF8
}

function Load-Global-Context(){
  Write-Info "Loading global context (index, presets, ambiguous terms)..."
  $ctx = [ordered]@{}
  $ctx.Index = if(Test-Path $indexFile){ Get-Content $indexFile -Raw } else { '' }
  $ctx.Presets = if(Test-Path $presetFile){ Get-Content $presetFile -Raw } else { '' }
  $ctx.Ambig = if(Test-Path $ambigFile){ Get-Content $ambigFile -Raw } else { '' }
  return $ctx
}

function Final-Audit(){
  Write-Info "Running final audit..."
  $idx = Get-Content $indexFile -Raw
  if($idx -notmatch 'knowledge_base:') { Write-Warn 'Index.yaml missing knowledge_base root.' }
  $bizFiles = Get-ChildItem -Path $kbRoot -Recurse -Filter '*.md'
  Write-Info ("Business documents count: {0}" -f $bizFiles.Count)
  Write-Info "Final audit complete."
}

while($true){
  Write-Info "Generating next batch..."
  & $genScript -SourceDir $SourceDir -TaskList $TaskList -BatchSize $BatchSize
  $code = $LASTEXITCODE
  if($code -eq 1){ Write-Info 'No more files. Proceeding to final audit.'; Final-Audit; break }
  if($code -ne 0){ throw "Batch generation failed with exit code $code" }

  $ctx = Load-Global-Context

  if(!(Test-Path $TaskList)){ throw "Task list $TaskList not found after generation." }
  $lines = Get-Content $TaskList | Where-Object { $_ -match '^\[ \] ' }
  foreach($line in $lines){
    $path = $line -replace '^\[ \]\s*',''
    if(!(Test-Path $path)){ Write-Warn "Source missing: $path"; Mark-Task-Complete $TaskList $path; continue }
    $dept = Get-DepartmentForFile -name ([IO.Path]::GetFileName($path))
    Create-OrUpdate-BusinessDoc -dept $dept -sourcePath $path
    Mark-Task-Complete -taskFile $TaskList -item $path
  }
  Write-Info "Batch processed and marked complete. Looping..."
}
