{"output":"from __future__ import annotations

from typing import Optional, Dict

import yaml
from functools import lru_cache
from pathlib import Path

from TelegramBot.config.settings import get_config


MODEL_OVERRIDE_PATH = Path(__file__).with_name("model_overrides.yaml")

@lru_cache(maxsize=1)
def _load_overrides() -> Dict[str, Dict[str, Dict[str, str]]]:
    if MODEL_OVERRIDE_PATH.exists():
        try:
            data = yaml.safe_load(MODEL_OVERRIDE_PATH.read_text(encoding="utf-8")) or {}
        except Exception:
            data = {}
    else:
        data = {}
    defaults = data.get("defaults", {})
    defaults = {str(k): str(v) for k, v in defaults.items()}
    bots = data.get("bots", {})
    cleaned_bots: Dict[str, Dict[str, str]] = {}
    for bot, mapping in bots.items():
        if isinstance(mapping, dict):
            cleaned_bots[str(bot)] = {str(k): str(v) for k, v in mapping.items()}
    return {"defaults": defaults, "bots": cleaned_bots}

def get_model_name(component: str, default: Optional[str] = None, bot_key: Optional[str] = None) -> str:
    """Return the model name for a given component with optional per-bot overrides."""
    overrides = _load_overrides()
    if bot_key is not None:
        bot_overrides = overrides["bots"].get(str(bot_key))
        if bot_overrides and component in bot_overrides:
            return bot_overrides[component]
    defaults = overrides["defaults"]
    if component in defaults:
        return defaults[component]

    config = get_config()
    env_overrides = config.get_openai_models()
    if component in env_overrides:
        return env_overrides[component]

    base = config.openai_model or default
    return base or "gpt-4o-mini"
