{"output":"from __future__ import annotations

import logging
import json
from typing import Dict, List

from TelegramBot.config.prompt_loader import get_prompt
from TelegramBot.config.model_config import get_model_name
from TelegramBot.config.settings import get_config
from TelegramBot.openai_runtime import create_response, extract_json

from .base import (
    AgentContext,
    BaseAgent,
    ReceptionResult,
    IntentDecision,
    KBAnswer,
    PlanStep,
    PlannerOutcome,
)
from .visa_kb_agent import VisaKnowledgeBaseAgent

logger = logging.getLogger(__name__)


class PlannerAgent(BaseAgent):
    """Turns intent decisions into structured plans and professional drafts."""

    def __init__(
        self,
        *,
        kb_agent: VisaKnowledgeBaseAgent,
        enable_kb: bool = True,
    ) -> None:
        self.config = get_config()
        self.kb_agent = kb_agent
        self.enable_kb = enable_kb
        self.system_prompt = get_prompt(
            "planner.system",
            default=(
                "You are a meticulous professional assistant. "
                "Given the conversation context, intent classification, and optional knowledge notes, "
                "you must produce JSON with the following shape:\n"
                "{\n"
                '  "status": "ok|need_more_info|escalate",\n'
                '  "draft_reply": "professional response for the receptionist to rephrase",\n'
                '  "notes": "optional bullet-style notes",\n'
                '  "questions": ["list of missing info if any"],\n'
                '  "actions": [{"type": "kb_lookup|llm_response|handoff", "payload": {...}}]\n'
                "}\n"
                "Keep the draft concise and factual. If more information is required, populate "
                '"status" with "need_more_info" and list precise questions.'
            ),
        )
        self.response_format = {
            "type": "json_schema",
            "json_schema": {
                "name": "planner_plan",
                "schema": {
                    "type": "object",
                    "properties": {
                        "status": {
                            "type": "string",
                            "enum": ["ok", "need_more_info", "escalate"],
                            "default": "ok",
                        },
                        "draft_reply": {"type": "string"},
                        "notes": {"type": "string"},
                        "questions": {
                            "type": "array",
                            "items": {"type": "string"},
                        },
                        "actions": {
                            "type": "array",
                            "items": {
                                "type": "object",
                                "properties": {
                                    "type": {"type": "string"},
                                    "payload": {"type": "object"},
                                },
                                "required": ["type"],
                            },
                        },
                    },
                    "required": ["status", "draft_reply"],
                },
            },
        }

    async def run(
        self,
        context: AgentContext,
        reception: ReceptionResult,
        intent: IntentDecision,
    ) -> PlannerOutcome:
        kb_answer: KBAnswer | None = None
        used_kb = False

        if self.enable_kb and intent.needs_kb:
            kb_answer = await self.kb_agent.run(
                query=reception.user_message,
                filters=intent.filters,
                conversation_preamble=reception.conversation_preamble,
                bot_key=str(context.bot_id),
            )
            used_kb = bool(kb_answer and kb_answer.answer)

        payload = {
            "user_message": reception.user_message,
            "chat_history": [
                {"role": msg.role, "content": msg.content}
                for msg in reception.trimmed_history[-8:]
            ],
            "intent": intent.raw_response or {
                "route": intent.route,
                "confidence": intent.confidence,
                "filters": intent.filters,
            },
            "knowledge_notes": kb_answer.answer if kb_answer and kb_answer.answer else "",
        }

        try:
            response = create_response(
                messages=[
                    {"role": "system", "content": self.system_prompt},
                    {"role": "user", "content": json_dumps(payload)},
                ],
                model=get_model_name("planner", default=self.config.openai_model, bot_key=str(context.bot_id)),
                response_format=self.response_format,
                temperature=0.2,
            )
            data = extract_json(response)
        except Exception as exc:  # pragma: no cover - defensive
            logger.error("Planner call failed, using fallback draft: %s", exc, exc_info=True)
            data = {}

        draft_reply = (data.get("draft_reply") or "").strip()
        if not draft_reply:
            draft_reply = "I'm unable to provide a detailed answer right now."

        actions_raw = data.get("actions") or []
        plan_steps: List[PlanStep] = []
        for item in actions_raw:
            if isinstance(item, dict):
                plan_steps.append(
                    PlanStep(
                        action=str(item.get("type", "llm_response")),
                        payload=item.get("payload", {}),
                    )
                )

        debug_info: Dict[str, Any] = {
            "planner_raw": data,
            "intent_route": intent.route,
            "intent_confidence": intent.confidence,
            "plan": [step.action for step in plan_steps],
        }
        if data.get("notes"):
            debug_info["notes"] = data["notes"]
        if data.get("questions"):
            debug_info["questions"] = data["questions"]

        outcome = PlannerOutcome(
            reply=draft_reply,
            used_kb=used_kb,
            kb_answer=kb_answer,
            debug_info=debug_info,
        )
        return outcome


def json_dumps(payload: Dict[str, Any]) -> str:
    return json.dumps(payload, ensure_ascii=False, indent=2)


