function Get-DepartmentForFile($name){
  $n = $name.ToLower()
  if($n -match '13a' -or $n -match 'ǩ֤' -or $n -match 'ecc' -or $n -match 'i-?card' -or $n -match 'aepȡ����ǩ') { return 'BearuOfImmigration' }
  if($n -match 'aep' -or $n -match 'labor') { return 'DepartmentOfLabor' }
  if($n -match 'nbi' -or $n -match 'doj') { return 'DepartmentOfJustice' }
  if($n -match 'passport' -or $n -match 'dfa' -or $n -match 'apostille') { return 'DepartmentOfForeignAffair' }
  if($n -match 'payment' -or $n -match 'dragonpay' -or $n -match 'bdo') { return 'PaymentServices' }
  if($n -match 'notar' -or $n -match 'certif' -or $n -match 'document') { return 'DocumentServices' }
  return 'GeneralServices'
}

function Ensure-DepartmentFolder($dept){
  $path = Join-Path $kbRoot $dept
  if(!(Test-Path $path)){ New-Item -ItemType Directory -Path $path | Out-Null }
  return $path
}

function Make-Slug([string]$text){
  $t = $text
  $t = [regex]::Replace($t,'\.[mM][dD]$','')
  $t = $t -replace '\s+', '-'
  $t = $t -replace '[^a-zA-Z0-9\-]','-'
  $t = $t.ToLower()
  $t = [regex]::Replace($t,'-+','-').Trim('-')
  if([string]::IsNullOrWhiteSpace($t)){ $t = (Get-Random).ToString() }
  return $t
}

function Update-IndexYaml($dept, $bizName, $relPath){
  $yaml = Get-Content $indexFile -Raw -ErrorAction SilentlyContinue
  if([string]::IsNullOrWhiteSpace($yaml)){ $yaml = "knowledge_base:`n  name: 'Visa KB'`n" }

  if($yaml -notmatch ("(?ms)^\s*" + [regex]::Escape($dept) + ":\s*")){
    $block = @"
  {0}:
