def _safe_getattr(obj: Any, name: str, default: int) -> int:
    value = getattr(obj, name, default)
    try:
        return int(value)
    except (TypeError, ValueError):
        return default


def _messages_to_text(messages: List[Dict[str, Any]]) -> str:
    fragments: List[str] = []
    for message in messages:
        content = message.get("content")
        if isinstance(content, str):
            fragments.append(content)
        elif isinstance(content, list):
            fragments.append(" ".join(str(part) for part in content))
    return "\n".join(fragments)


def get_usage_snapshot(response: Any) -> Dict[str, Any]:
    return getattr(response, "_usage_snapshot", {}) or {}


def parse_structured_content(response: Any, model_cls: Type[Any]) -> Any:
    """Expose structured output parsing for follow-up responses (e.g., after tool outputs)."""
    return _extract_structured_output(response, model_cls)


def _extract_parsed_content(response: Any) -> Any:
    output = getattr(response, "output", None)
    if not output:
        return None
    for block in output:
        for item in getattr(block, "content", []):
            parsed = getattr(item, "parsed", None)
            if parsed is not None:
                return parsed
    return None


def _extract_structured_output(response: Any, model_cls: Type[Any]) -> Any:
    output = getattr(response, "output", None)
    if not output:
        return None

    refusal_reason = None
    parsed_json_str = None

    for block in output:
        content_items = getattr(block, "content", None)
        if not content_items:
            continue
        for item in content_items:
            item_type = getattr(item, "type", None)
            if item_type == "refusal":
                refusal_reason = getattr(item, "reason", None) or "refused"
            if item_type == "output_text":
                text = getattr(item, "text", "") or ""
                stripped = text.strip()
                if stripped:
                    parsed_json_str = stripped
                    break
        if parsed_json_str:
            break

    if parsed_json_str is None:
        if refusal_reason:
            raise ValueError(f"Model refused to comply: {refusal_reason}")
        return None

    data = json.loads(parsed_json_str)
    if hasattr(model_cls, "model_validate"):
        return model_cls.model_validate(data)
    if hasattr(model_cls, "parse_obj"):
        return model_cls.parse_obj(data)
    return data


def _apply_temperature(request: Dict[str, Any], model: Optional[str], temperature: Optional[float]) -> None:
    """Attach temperature when the target model supports sampling controls."""
    if temperature is None:
        return

    if _supports_temperature(model):
        request["temperature"] = temperature
    else:
        logger.debug(
            "Skipping temperature parameter for model '%s' because reasoning models require the default.",
            model,
        )


def _supports_temperature(model: Optional[str]) -> bool:
    if not model:
        return True

    normalized = model.lower()
    for sep in (":", "/"):
        if sep in normalized:
            normalized = normalized.split(sep, 1)[1]

    explicit_allow = (
        "gpt-5-chat",
        "gpt-5.1-chat",
        "gpt-5.0-chat",
        "gpt-5-codex",
    )
    if any(normalized.startswith(prefix) for prefix in explicit_allow):
        return True

    reasoning_prefixes = (
        "gpt-5",
        "gpt-5-mini",
        "gpt-5-nano",
        "gpt-5-pro",
        "o1",
        "o3",
        "o4",
    )
    return not any(normalized.startswith(prefix) for prefix in reasoning_prefixes)
