import logging
import threading
from dataclasses import dataclass
from typing import Dict, Mapping

from fastapi import HTTPException

from TelegramBot.config.settings import BotInstance, TelegramBotConfig, get_config
from TelegramBot.handlers.message_entry_handler import MessageEntryHandler, create_message_handler
from TelegramBot.services.telegram_service import (
    TelegramService,
    create_telegram_service,
)
from SharedUtility.RichLogger.logger import RichLoggerManager

RichLoggerManager.bootstrap()
logger = logging.getLogger(__name__)


@dataclass
class BotHandlerContext:
    bot: BotInstance
    handler: MessageEntryHandler


class BotRegistry:
    def __init__(self, config: TelegramBotConfig | None = None) -> None:
        self._config = config or get_config()
        self._service_cache: Dict[str, TelegramService] = {}
        self._handler_cache: Dict[str, MessageEntryHandler] = {}
        self._service_lock = threading.Lock()
        self._handler_lock = threading.Lock()

    @property
    def config(self) -> TelegramBotConfig:
        return self._config

    def resolve_bot(self, bot_name: str) -> BotInstance:
        bot = self._config.get_bot_by_name(bot_name)
        if not bot:
            logger.warning("Bot lookup failed: %s", bot_name)
            raise HTTPException(status_code=404, detail=f"Bot '{bot_name}' not found")
        return bot

    def resolve_handler(self, bot_name: str) -> BotHandlerContext:
        bot = self.resolve_bot(bot_name)
        handler = self._get_or_create_handler(bot.token)
        return BotHandlerContext(bot=bot, handler=handler)

    def resolve_service(self, bot_name: str) -> tuple[BotInstance, TelegramService]:
        bot = self.resolve_bot(bot_name)
        service = self._get_or_create_service(bot.token)
        return bot, service

    def get_service_for_bot(self, bot: BotInstance) -> TelegramService:
        return self._get_or_create_service(bot.token)

    def ensure_secret(self, headers: Mapping[str, str]) -> None:
        secret = self._config.telegram_webhook_secret
        if not secret:
            return
        token = headers.get("X-Telegram-Bot-Api-Secret-Token")
        if token != secret:
            logger.warning("Webhook secret mismatch")
            raise HTTPException(status_code=403, detail="Invalid secret token")

    def list_bots(self) -> list[BotInstance]:
        return self._config.get_bots()

    def _get_or_create_service(self, token: str) -> TelegramService:
        with self._service_lock:
            service = self._service_cache.get(token)
            if service is None:
                service = create_telegram_service(token)
                self._service_cache[token] = service
        return service

    def _get_or_create_handler(self, token: str) -> MessageEntryHandler:
        with self._handler_lock:
            handler = self._handler_cache.get(token)
            if handler is None:
                handler = create_message_handler(token)
                self._handler_cache[token] = handler
        return handler

    async def shutdown(self) -> None:
        with self._service_lock:
            services = list(self._service_cache.values())
            self._service_cache.clear()
        with self._handler_lock:
            handlers = list(self._handler_cache.values())
            self._handler_cache.clear()

        for service in services:
            try:
                await service.close()
            except Exception as exc:
                logger.error("Failed to close Telegram service: %s", exc, exc_info=True)

        for handler in handlers:
            try:
                await handler.close()
            except Exception as exc:
                logger.error("Failed to close message handler: %s", exc, exc_info=True)


bot_registry = BotRegistry()
